using System.Collections;using System.Collections.Generic;using UnityEngine;using UnityEngine.SceneManagement;public class LevelManager : MonoBehaviour {    public bool timedLevel = false;    public float timeRemaining = 20.0f;	// Use this for initialization	void Start () {        	}		// Update is called once per frame	void Update () {        if(timedLevel)        {            timeRemaining -= Time.deltaTime;            if(timeRemaining <0)            {                LoadNextScene();            }        }        	}    public void LoadNextScene()    {        int currentSceneIndex = SceneManager.GetActiveScene().buildIndex; // get the current scene index                SceneManager.LoadScene(currentSceneIndex + 1); // load next scene    }    public void LoadMenu()    {        SceneManager.LoadScene(0); // load next scene    }    public void LoadMainGame()    {        SceneManager.LoadScene(1); // load next scene    }}